                <script>
                    $(document).ready(function () {
                        var template = shop.template;
                        $(".lang_vi").click(function (event) {
                            event.preventDefault();
                            var href = window.location.origin + window.location.pathname;
                            var local = localStorage.getItem('template');
                            if (local == 'theme') {
                                window.location = href;
                            } else {
                                localStorage.setItem('template', 'theme');
                                if ($(".temproduct").html() == 'product') {
                                    window.location = href;
                                } else if ($(".temproduct").html() == 'collection') {
                                    window.location = href;
                                } else if (template == 'page.en') { // ki?m tra là có , không có là  == -1
                                    window.location = href;
                                } else if ($(".cochuyentrang").length > 0) {
                                    var url = $(".cochuyentrang").html();
                                    location.href = url;
                                } else if ($(".temproduct").html() == 'pagekhongchuan') {
                                    window.location = '/';
                                } else if (template.indexOf('search') > -1) { // ki?m tra là có , không có là  == -1
                                    window.location = window.location.href.replace('&view=en', '');
                                } else {
                                    window.location = href;
                                }
                            }
                        })

                        $(".lang_en").click(function (event) {
                            event.preventDefault();
                            var href = window.location.origin + window.location.pathname;
                            var local = localStorage.getItem('template');
                            if (local == 'en') {
                                window.location = href;
                            } else {
                                localStorage.setItem('template', 'en');
                                if ($(".temproduct").html() == 'product') {
                                    var view = $(".checkview").html();
                                    var linkchuan = location.href;
                                    var n = linkchuan.includes("?");
                                    if (n == true) {
                                        var inview = '&view=';
                                    } else {
                                        var inview = '?view=';
                                    }
                                    var viewend = $(".chuanview").html();
                                    if (viewend == 'product') {
                                        var viewend = 'en';
                                    } else {
                                        var viewend = '_en';
                                    }
                                    window.location = href + inview + view + viewend;
                                } else if ($(".cochuyentrang").length > 0) {
                                    var url = $(".cochuyentrang").html();
                                    location.href = url;
                                } else if ($(".temproduct").html() == 'collection') {
                                    var view = $(".checkview").html();
                                    var linkchuan = location.href;
                                    var n = linkchuan.includes("?");
                                    if (n == true) {
                                        var inview = '&view=';
                                    } else {
                                        var inview = '?view=';
                                    }
                                    var viewend = $(".chuanview").html();
                                    if (viewend == 'collection') {
                                        var viewend = 'en';
                                    } else {
                                        var viewend = '_en';
                                    }
                                    window.location = href + inview + view + viewend;
                                } else if (template == 'page') {
                                    var linkchuan = location.href;
                                    var n = linkchuan.includes("?");
                                    if (n == true) {
                                        var inview = '&view=en';
                                    } else {
                                        var inview = '?view=en';
                                    }
                                    window.location = href + inview;
                                } else if ($(".cochuyentrang").length > 0) {
                                    var url = $(".cochuyentrang").html();
                                    location.href = url;
                                } else if ($(".temproduct").html() == 'pagekhongchuan') {
                                    window.location = '/?view=en';
                                } else if (template.indexOf('search') > -1) {
                                    window.location = window.location.href + "&view=en"
                                } else {
                                    window.location = href + "?view=en";
                                }
                            }

                        })

                    })
                </script>


