            <script>
                $(document).ready(function () {
                    if ($(window).width() < 768) {
                        $('.top-header.hidden-xs li.choose-store').remove();
                    } else {
                        $('nav.navbar-main.navbar li.choose-store').remove();
                    }
                    var cartGet = null;
                    var newStoreGet = [];
                    var store = [

                        {
                            "name": "Moonmilk- DN- Nguyễn Văn Thoại",
